import { useState, useEffect } from "react";
import { Star, Send, User, Calendar } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";

interface Review {
  id: string;
  name: string;
  rating: number;
  message: string;
  date: string;
}

const STORAGE_KEY = "dr_samer_reviews";

const loadReviews = (): Review[] => {
  try {
    const stored = localStorage.getItem(STORAGE_KEY);
    return stored ? JSON.parse(stored) : [];
  } catch {
    return [];
  }
};

const addReview = (review: Review): void => {
  const reviews = loadReviews();
  reviews.unshift(review);
  localStorage.setItem(STORAGE_KEY, JSON.stringify(reviews));
};

const FeedbackReviews = () => {
  const [reviews, setReviews] = useState<Review[]>([]);
  const [name, setName] = useState("");
  const [rating, setRating] = useState(0);
  const [hoverRating, setHoverRating] = useState(0);
  const [message, setMessage] = useState("");
  const [errors, setErrors] = useState<{ name?: string; rating?: string; message?: string }>({});
  const [success, setSuccess] = useState(false);

  useEffect(() => {
    setReviews(loadReviews());
  }, []);

  const validateForm = (): boolean => {
    const newErrors: { name?: string; rating?: string; message?: string } = {};
    
    if (!name.trim()) {
      newErrors.name = "Name is required";
    }
    if (rating === 0) {
      newErrors.rating = "Please select a rating";
    }
    if (!message.trim()) {
      newErrors.message = "Message is required";
    }
    
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    
    if (!validateForm()) return;

    const newReview: Review = {
      id: Date.now().toString(),
      name: name.trim(),
      rating,
      message: message.trim(),
      date: new Date().toLocaleDateString("en-US", {
        month: "short",
        day: "numeric",
        year: "numeric",
      }),
    };

    addReview(newReview);
    setReviews(loadReviews());
    
    setName("");
    setRating(0);
    setMessage("");
    setErrors({});
    setSuccess(true);
    
    setTimeout(() => setSuccess(false), 3000);
  };

  const renderStars = (count: number, interactive = false, size = "w-5 h-5") => {
    return (
      <div className="flex gap-1">
        {[1, 2, 3, 4, 5].map((star) => (
          <button
            key={star}
            type="button"
            disabled={!interactive}
            className={`transition-all duration-200 ${interactive ? "cursor-pointer hover:scale-110" : "cursor-default"}`}
            onMouseEnter={() => interactive && setHoverRating(star)}
            onMouseLeave={() => interactive && setHoverRating(0)}
            onClick={() => interactive && setRating(star)}
          >
            <Star
              className={`${size} transition-colors duration-200 ${
                star <= (interactive ? hoverRating || rating : count)
                  ? "fill-yellow-400 text-yellow-400"
                  : "fill-transparent text-muted-foreground/40"
              }`}
            />
          </button>
        ))}
      </div>
    );
  };

  return (
    <section id="feedback" className="py-20 md:py-28 bg-gradient-to-b from-background to-card/30">
      <div className="container mx-auto px-4 md:px-6">
        {/* Section Header */}
        <div className="text-center mb-12 md:mb-16">
          <span className="inline-block px-4 py-1.5 mb-4 text-xs font-medium tracking-wider uppercase bg-primary/10 text-primary rounded-full">
            Your Voice Matters
          </span>
          <h2 className="text-3xl md:text-4xl lg:text-5xl font-bold mb-4 bg-gradient-to-r from-foreground via-foreground to-primary bg-clip-text">
            Patient Feedback
          </h2>
          <p className="text-muted-foreground text-base md:text-lg max-w-2xl mx-auto">
            Share your experience with Dr. Samer Farhat
          </p>
        </div>

        <div className="max-w-4xl mx-auto">
          {/* Review Form */}
          <div className="bg-card/60 backdrop-blur-sm border border-border/50 rounded-2xl p-6 md:p-8 mb-12 shadow-lg">
            <h3 className="text-xl font-semibold mb-6 flex items-center gap-2">
              <Star className="w-5 h-5 text-primary" />
              Leave a Review
            </h3>
            
            <form onSubmit={handleSubmit} className="space-y-5">
              {/* Name Input */}
              <div>
                <label className="block text-sm font-medium mb-2">Your Name</label>
                <Input
                  type="text"
                  placeholder="Enter your name"
                  value={name}
                  onChange={(e) => setName(e.target.value)}
                  className="bg-background/50 border-border/50 focus:border-primary"
                />
                {errors.name && (
                  <p className="text-destructive text-xs mt-1.5">{errors.name}</p>
                )}
              </div>

              {/* Star Rating */}
              <div>
                <label className="block text-sm font-medium mb-2">Your Rating</label>
                <div className="flex items-center gap-3">
                  {renderStars(rating, true, "w-7 h-7")}
                  {rating > 0 && (
                    <span className="text-sm text-muted-foreground">
                      {rating} star{rating > 1 ? "s" : ""}
                    </span>
                  )}
                </div>
                {errors.rating && (
                  <p className="text-destructive text-xs mt-1.5">{errors.rating}</p>
                )}
              </div>

              {/* Message Textarea */}
              <div>
                <label className="block text-sm font-medium mb-2">Your Message</label>
                <Textarea
                  placeholder="Share your experience..."
                  value={message}
                  onChange={(e) => setMessage(e.target.value)}
                  rows={4}
                  className="bg-background/50 border-border/50 focus:border-primary resize-none"
                />
                {errors.message && (
                  <p className="text-destructive text-xs mt-1.5">{errors.message}</p>
                )}
              </div>

              {/* Submit Button */}
              <Button
                type="submit"
                className="w-full md:w-auto px-8 py-2.5 bg-primary hover:bg-primary/90 text-primary-foreground font-medium rounded-lg transition-all duration-300 hover:shadow-lg hover:shadow-primary/25"
              >
                <Send className="w-4 h-4 mr-2" />
                Submit Review
              </Button>

              {/* Success Message */}
              {success && (
                <div className="flex items-center gap-2 text-green-400 text-sm animate-fade-in">
                  <div className="w-2 h-2 bg-green-400 rounded-full" />
                  Thank you! Your review has been submitted successfully.
                </div>
              )}
            </form>
          </div>

          {/* Reviews List */}
          <div>
            <h3 className="text-xl font-semibold mb-6 flex items-center gap-2">
              <User className="w-5 h-5 text-primary" />
              Recent Reviews
              {reviews.length > 0 && (
                <span className="text-sm font-normal text-muted-foreground">
                  ({reviews.length})
                </span>
              )}
            </h3>

            {reviews.length === 0 ? (
              <div className="text-center py-12 bg-card/40 rounded-2xl border border-border/30">
                <Star className="w-12 h-12 text-muted-foreground/30 mx-auto mb-3" />
                <p className="text-muted-foreground">No reviews yet. Be the first to share your experience!</p>
              </div>
            ) : (
              <div className="grid gap-4 md:gap-6">
                {reviews.map((review) => (
                  <div
                    key={review.id}
                    className="bg-card/60 backdrop-blur-sm border border-border/50 rounded-xl p-5 md:p-6 shadow-md hover:shadow-lg transition-all duration-300 hover:border-primary/30 animate-fade-in"
                  >
                    <div className="flex flex-col md:flex-row md:items-start md:justify-between gap-3 mb-3">
                      <div className="flex items-center gap-3">
                        <div className="w-10 h-10 rounded-full bg-primary/20 flex items-center justify-center">
                          <span className="text-primary font-semibold text-sm">
                            {review.name.charAt(0).toUpperCase()}
                          </span>
                        </div>
                        <div>
                          <h4 className="font-medium text-foreground">{review.name}</h4>
                          <div className="flex items-center gap-2 mt-0.5">
                            {renderStars(review.rating)}
                          </div>
                        </div>
                      </div>
                      <div className="flex items-center gap-1.5 text-xs text-muted-foreground md:text-right">
                        <Calendar className="w-3.5 h-3.5" />
                        {review.date}
                      </div>
                    </div>
                    <p className="text-muted-foreground text-sm md:text-base leading-relaxed pl-0 md:pl-13">
                      {review.message}
                    </p>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>
      </div>
    </section>
  );
};

export default FeedbackReviews;
