import { MapPin, Clock, Phone } from "lucide-react";

const locations = [
  {
    name: "Beirut Clinic",
    address: "Chiyah – Mar Mkhayel Church – Near Mazen Pharmacy",
    phone: "+961 76 026 004",
    hours: [
      { days: "Monday – Friday", time: "9:00 AM – 7:00 PM" },
      { days: "Saturday", time: "10:00 AM – 2:00 PM" },
      { days: "Sunday", time: "Closed" },
    ],
    mapUrl:
      "https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d3312.5!2d35.5!3d33.87!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x0%3A0x0!2zMzPCsDUyJzEyLjAiTiAzNcKwMzAnMDAuMCJF!5e0!3m2!1sen!2slb!4v1",
  },
  {
    name: "Tripoli Clinic",
    address: "Tripoli – Main Street – Near City Center",
    phone: "+961 76 026 004",
    hours: [
      { days: "Monday – Friday", time: "9:00 AM – 7:00 PM" },
      { days: "Saturday", time: "10:00 AM – 2:00 PM" },
      { days: "Sunday", time: "Closed" },
    ],
    mapUrl:
      "https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d3312.5!2d35.84!3d34.43!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x0%3A0x0!2zMzTCsDI1JzQ4LjAiTiAzNcKwNTAnMjQuMCJF!5e0!3m2!1sen!2slb!4v1",
  },
];

const Locations = () => {
  return (
    <section id="locations" className="py-12 md:py-20 bg-secondary/30">
      <div className="max-w-6xl mx-auto px-4 sm:px-6">
        {/* Header */}
        <div className="text-center max-w-2xl mx-auto mb-8 md:mb-12 animate-fade-in-up">
          <h3 className="text-sm font-semibold text-primary uppercase tracking-wide mb-2">
            Our Clinics
          </h3>
          <h2 className="text-2xl sm:text-3xl md:text-4xl font-bold text-foreground mb-3">
            Visit Us
          </h2>
          <p className="text-sm md:text-base text-muted-foreground">
            We have two convenient locations to serve you better.
          </p>
        </div>

        {/* Locations Grid */}
        <div className="grid md:grid-cols-2 gap-6 md:gap-8">
          {locations.map((location, index) => (
            <div
              key={location.name}
              className="bg-card border border-border rounded-2xl shadow-lg overflow-hidden animate-fade-in-up hover:shadow-xl transition-all duration-500"
              style={{ animationDelay: `${index * 150}ms` }}
            >
              {/* Map Embed */}
              <div className="aspect-video w-full">
                <iframe
                  src={location.mapUrl}
                  width="100%"
                  height="100%"
                  style={{ border: 0 }}
                  allowFullScreen
                  loading="lazy"
                  referrerPolicy="no-referrer-when-downgrade"
                  title={`${location.name} Map`}
                  className="w-full h-full"
                />
              </div>

              {/* Location Details */}
              <div className="p-5 md:p-6 space-y-4">
                <h3 className="text-xl font-bold text-foreground flex items-center gap-2">
                  <MapPin className="w-5 h-5 text-primary" />
                  {location.name}
                </h3>

                <p className="text-sm text-muted-foreground">{location.address}</p>

                <a
                  href={`tel:${location.phone.replace(/\s/g, "")}`}
                  className="flex items-center gap-2 text-sm text-primary hover:text-accent transition-colors"
                >
                  <Phone className="w-4 h-4" />
                  {location.phone}
                </a>

                {/* Working Hours */}
                <div className="pt-3 border-t border-border">
                  <h4 className="text-sm font-semibold text-foreground flex items-center gap-2 mb-3">
                    <Clock className="w-4 h-4 text-primary" />
                    Working Hours
                  </h4>
                  <ul className="space-y-1.5">
                    {location.hours.map((schedule) => (
                      <li
                        key={schedule.days}
                        className="flex justify-between text-sm"
                      >
                        <span className="text-muted-foreground">{schedule.days}</span>
                        <span
                          className={
                            schedule.time === "Closed"
                              ? "text-destructive font-medium"
                              : "text-foreground font-medium"
                          }
                        >
                          {schedule.time}
                        </span>
                      </li>
                    ))}
                  </ul>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Locations;
