import { Activity, Sparkles, Stethoscope, Smile } from "lucide-react";

const Services = () => {
  const services = [
    {
      icon: Activity,
      title: "Endodontics",
      description:
        "Advanced root canal therapy using the latest technology to save your natural teeth and eliminate pain.",
    },
    {
      icon: Sparkles,
      title: "Hollywood Smile & Veneers",
      description:
        "Transform your smile with custom-designed porcelain veneers for a perfect, natural-looking Hollywood smile.",
    },
    {
      icon: Stethoscope,
      title: "Dental Implants",
      description:
        "Permanent tooth replacement solutions using premium implants for a complete and confident smile.",
    },
    {
      icon: Smile,
      title: "Cosmetic Dentistry",
      description:
        "Comprehensive aesthetic treatments including teeth whitening, bonding, and smile makeovers.",
    },
  ];

  return (
    <section id="services" className="py-20 bg-gradient-to-b from-card to-background">
      <div className="container mx-auto px-4">
        <div className="text-center mb-16 fade-in-up">
          <div className="inline-block bg-primary/10 text-primary px-4 py-2 rounded-full text-sm font-semibold mb-4">
            Our Services
          </div>
          <h2 className="section-title">
            Specialized Dental Services
          </h2>
          <p className="text-muted-foreground text-lg max-w-2xl mx-auto mt-4">
            Comprehensive dental care tailored to your unique needs with advanced
            technology and personalized attention.
          </p>
        </div>

        <div className="grid md:grid-cols-2 lg:grid-cols-4 gap-6">
          {services.map((service, index) => {
            const Icon = service.icon;
            return (
              <div
                key={service.title}
                className="card-medical p-8 group hover:scale-105 cursor-pointer fade-in-up"
                style={{ animationDelay: `${index * 100}ms` }}
              >
                <div className="w-16 h-16 bg-primary/10 rounded-2xl flex items-center justify-center mb-6 group-hover:bg-primary transition-colors duration-300">
                  <Icon className="w-8 h-8 text-primary group-hover:text-primary-foreground transition-colors duration-300" />
                </div>
                <h3 className="text-xl font-bold mb-3 text-foreground">
                  {service.title}
                </h3>
                <p className="text-muted-foreground leading-relaxed">
                  {service.description}
                </p>
              </div>
            );
          })}
        </div>
      </div>
    </section>
  );
};

export default Services;
