import { Star } from "lucide-react";
import testimonial1 from "@/assets/testimonial-1.jpg";
import testimonial2 from "@/assets/testimonial-2.jpg";
import testimonial3 from "@/assets/testimonial-3.jpg";

const Testimonials = () => {
  const testimonials = [
    {
      name: "Rebecca",
      location: "London",
      image: testimonial1,
      rating: 5,
      text: "Dr. Farhat transformed my smile completely! The Hollywood smile treatment was painless and the results are absolutely stunning. Highly recommend!",
    },
    {
      name: "Mark",
      location: "New York",
      image: testimonial2,
      rating: 5,
      text: "Professional service and exceptional care. My dental implants look and feel natural. Dr. Farhat's expertise is outstanding.",
      featured: true,
    },
    {
      name: "Anderson",
      location: "London",
      image: testimonial3,
      rating: 5,
      text: "The root canal treatment was surprisingly comfortable. Dr. Farhat explained everything clearly and made me feel at ease throughout the procedure.",
    },
  ];

  return (
    <section className="py-20 bg-card">
      <div className="container mx-auto px-4">
        <div className="text-center mb-16 fade-in-up">
          <div className="inline-block bg-primary/10 text-primary px-4 py-2 rounded-full text-sm font-semibold mb-4">
            Testimonials
          </div>
          <h2 className="section-title">
            What Our Patients Say
          </h2>
          <p className="text-muted-foreground text-lg max-w-2xl mx-auto mt-4">
            Read testimonials from our satisfied patients who have experienced
            exceptional dental care.
          </p>
        </div>

        <div className="grid md:grid-cols-3 gap-6 max-w-6xl mx-auto">
          {testimonials.map((testimonial, index) => (
            <div
              key={testimonial.name}
              className={`rounded-3xl p-8 transition-all duration-300 hover:scale-105 fade-in-up ${
                testimonial.featured
                  ? "bg-primary text-primary-foreground shadow-2xl"
                  : "bg-background shadow-lg"
              }`}
              style={{ animationDelay: `${index * 100}ms` }}
            >
              <div className="flex items-center gap-4 mb-4">
                <img
                  src={testimonial.image}
                  alt={testimonial.name}
                  className="w-16 h-16 rounded-full object-cover border-4 border-card"
                />
                <div>
                  <h3 className="font-bold text-lg">{testimonial.name}</h3>
                  <p
                    className={
                      testimonial.featured
                        ? "text-primary-foreground/80 text-sm"
                        : "text-muted-foreground text-sm"
                    }
                  >
                    {testimonial.location}
                  </p>
                </div>
              </div>

              <div className="flex gap-1 mb-4">
                {Array.from({ length: testimonial.rating }).map((_, i) => (
                  <Star
                    key={i}
                    className={`w-5 h-5 ${
                      testimonial.featured
                        ? "fill-primary-foreground text-primary-foreground"
                        : "fill-primary text-primary"
                    }`}
                  />
                ))}
              </div>

              <p
                className={
                  testimonial.featured
                    ? "text-primary-foreground/90 leading-relaxed"
                    : "text-muted-foreground leading-relaxed"
                }
              >
                "{testimonial.text}"
              </p>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Testimonials;
