+
+/* Definition of the design system. All colors, gradients, fonts, etc should be defined here. 
+All colors MUST be HSL.
+*/
+
+@layer base {
+  :root {
+    --background: 0 0% 100%;
+    --foreground: 215 25% 27%;
+
+    --card: 0 0% 100%;
+    --card-foreground: 215 25% 27%;
+
+    --popover: 0 0% 100%;
+    --popover-foreground: 215 25% 27%;
+
+    --primary: 210 85% 45%;
+    --primary-foreground: 0 0% 100%;
+
+    --secondary: 210 40% 96%;
+    --secondary-foreground: 215 25% 27%;
+
+    --muted: 210 35% 95%;
+    --muted-foreground: 215 15% 50%;
+
+    --accent: 210 90% 55%;
+    --accent-foreground: 0 0% 100%;
+
+    --destructive: 0 84% 60%;
+    --destructive-foreground: 0 0% 100%;
+
+    --border: 210 25% 88%;
+    --input: 210 25% 88%;
+    --ring: 210 85% 45%;
+
+    --radius: 0.75rem;
+
+    /* Medical theme shadows */
+    --shadow-sm: 0 1px 2px 0 hsl(210 25% 88% / 0.3);
+    --shadow-md: 0 4px 6px -1px hsl(210 25% 88% / 0.3), 0 2px 4px -2px hsl(210 25% 88% / 0.3);
+    --shadow-lg: 0 10px 15px -3px hsl(210 25% 88% / 0.3), 0 4px 6px -4px hsl(210 25% 88% / 0.3);
+  }
+}
+
+@layer base {
+  * {
+    @apply border-border;
+  }
+
+  body {
+    @apply bg-background text-foreground;
+  }
+}

