import { useEffect } from "react";
import { useLocation } from "react-router-dom";
import { Sparkles, Star, Phone, MessageCircle, Award, Shield } from "lucide-react";
import { Button } from "@/components/ui/button";
import Navbar from "@/components/Navbar";
import Footer from "@/components/Footer";
import cosmeticImg from "@/assets/cosmetic-dentistry.jpg";
import veneersImg from "@/assets/veneers.jpg";
import smileImg from "@/assets/smile-makeover.jpg";
import whiteningImg from "@/assets/whitening.jpg";

const CosmeticDentistry = () => {
  const location = useLocation();

  useEffect(() => {
    // Handle hash scrolling after page loads
    if (location.hash) {
      setTimeout(() => {
        const element = document.querySelector(location.hash);
        if (element) {
          element.scrollIntoView({ behavior: "smooth" });
        }
      }, 100);
    } else {
      window.scrollTo(0, 0);
    }
  }, [location]);

  const emaxImages = [cosmeticImg, veneersImg, smileImg, whiteningImg];
  const compositeImages = [smileImg, whiteningImg, veneersImg, cosmeticImg];

  return (
    <div className="min-h-screen bg-vip">
      <Navbar />

      {/* Hero Section */}
      <section className="pt-32 pb-20 relative overflow-hidden">
        {/* Gold gradient background */}
        <div className="absolute inset-0 bg-gradient-to-br from-vip-cream via-vip to-white" />
        <div className="absolute inset-0 opacity-30 bg-[radial-gradient(ellipse_at_top_right,_hsl(43_74%_52%_/_0.2),_transparent_50%)]" />
        
        {/* Subtle shine effect */}
        <div className="absolute top-0 right-0 w-1/2 h-full opacity-10">
          <div className="absolute top-20 right-20 w-96 h-96 bg-gold rounded-full blur-3xl" />
        </div>

        <div className="container mx-auto px-4 relative z-10">
          <div className="max-w-4xl mx-auto text-center animate-fade-in">
            <div className="inline-flex items-center gap-2 bg-gold/10 border border-gold/30 text-gold-dark px-4 py-2 rounded-full mb-6">
              <Sparkles className="w-4 h-4" />
              <span className="text-sm font-medium">Premium VIP Service</span>
            </div>
            
            <h1 className="text-5xl md:text-6xl lg:text-7xl font-bold text-foreground mb-6">
              Cosmetic <span className="text-gold">Dentistry</span>
            </h1>
            
            <p className="text-xl md:text-2xl text-muted-foreground max-w-2xl mx-auto">
              Luxury Smile Design & Aesthetic Excellence
            </p>

            <div className="mt-10 flex flex-wrap justify-center gap-4">
              <div className="flex items-center gap-2 text-muted-foreground">
                <Award className="w-5 h-5 text-gold" />
                <span>Premium Materials</span>
              </div>
              <div className="flex items-center gap-2 text-muted-foreground">
                <Shield className="w-5 h-5 text-gold" />
                <span>10-Year Warranty</span>
              </div>
              <div className="flex items-center gap-2 text-muted-foreground">
                <Star className="w-5 h-5 text-gold" />
                <span>VIP Experience</span>
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* E-max Veneers Section */}
      <section id="emax-veneers" className="py-20 bg-white">
        <div className="container mx-auto px-4">
          <div className="max-w-6xl mx-auto">
            <div className="flex flex-col lg:flex-row gap-12 items-start">
              {/* Content */}
              <div className="lg:w-1/2 animate-fade-in">
                <div className="inline-flex items-center gap-2 bg-primary/10 text-primary px-3 py-1.5 rounded-full mb-4">
                  <Award className="w-4 h-4" />
                  <span className="text-xs font-semibold uppercase tracking-wider">Premium Ceramic Veneers</span>
                </div>
                
                <h2 className="text-4xl md:text-5xl font-bold text-foreground mb-6">
                  E-max Veneers
                </h2>
                
                <p className="text-lg text-muted-foreground mb-6 leading-relaxed">
                  Experience the pinnacle of aesthetic dentistry with our E-max veneers. 
                  Crafted from lithium disilicate glass-ceramic, these premium veneers offer 
                  unparalleled translucency that mimics natural tooth enamel perfectly.
                </p>
                
                <ul className="space-y-3 mb-8">
                  {[
                    "Superior durability lasting 15+ years",
                    "Natural translucency matching real teeth",
                    "Stain-resistant and biocompatible",
                    "Minimal tooth preparation required"
                  ].map((item, i) => (
                    <li key={i} className="flex items-center gap-3 text-foreground">
                      <div className="w-2 h-2 rounded-full bg-gold" />
                      {item}
                    </li>
                  ))}
                </ul>
              </div>

              {/* Image Gallery */}
              <div className="lg:w-1/2 grid grid-cols-2 gap-4 animate-fade-in" style={{ animationDelay: "150ms" }}>
                {emaxImages.map((img, i) => (
                  <div 
                    key={i} 
                    className={`relative rounded-2xl overflow-hidden shadow-lg ${i === 0 ? "col-span-2 h-64" : "h-48"}`}
                  >
                    <img 
                      src={img} 
                      alt={`E-max Veneers example ${i + 1}`}
                      className="w-full h-full object-cover hover:scale-105 transition-transform duration-500"
                    />
                  </div>
                ))}
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* VIP Composite Veneers Section */}
      <section id="vip-composite" className="py-20 bg-gradient-to-b from-vip-cream to-vip relative overflow-hidden">
        {/* Subtle gold glow */}
        <div className="absolute top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2 w-[600px] h-[600px] bg-gold/5 rounded-full blur-3xl" />
        
        <div className="container mx-auto px-4 relative z-10">
          <div className="max-w-6xl mx-auto">
            <div className="flex flex-col lg:flex-row-reverse gap-12 items-start">
              {/* Content */}
              <div className="lg:w-1/2 animate-fade-in">
                <div className="inline-flex items-center gap-2 bg-gold/20 text-gold-dark px-3 py-1.5 rounded-full mb-4 border border-gold/30">
                  <Star className="w-4 h-4 fill-gold text-gold" />
                  <span className="text-xs font-semibold uppercase tracking-wider">VIP Treatment</span>
                </div>
                
                <h2 className="text-4xl md:text-5xl font-bold text-foreground mb-6">
                  VIP Composite <span className="text-gold">Veneers</span>
                </h2>
                
                <p className="text-lg text-muted-foreground mb-6 leading-relaxed">
                  Achieve a stunning smile transformation in just one visit with our VIP Composite Veneers. 
                  These expertly sculpted veneers offer exceptional aesthetics at an accessible price point, 
                  with the flexibility for easy adjustments and repairs.
                </p>
                
                <ul className="space-y-3 mb-8">
                  {[
                    "Same-day smile transformation",
                    "Highly customizable shade matching",
                    "Non-invasive and reversible procedure",
                    "Affordable luxury option"
                  ].map((item, i) => (
                    <li key={i} className="flex items-center gap-3 text-foreground">
                      <div className="w-2 h-2 rounded-full bg-gold" />
                      {item}
                    </li>
                  ))}
                </ul>
              </div>

              {/* Image Gallery */}
              <div className="lg:w-1/2 grid grid-cols-2 gap-4 animate-fade-in" style={{ animationDelay: "150ms" }}>
                {compositeImages.map((img, i) => (
                  <div 
                    key={i} 
                    className={`relative rounded-2xl overflow-hidden shadow-lg ${i === 0 ? "col-span-2 h-64" : "h-48"}`}
                  >
                    <img 
                      src={img} 
                      alt={`VIP Composite Veneers example ${i + 1}`}
                      className="w-full h-full object-cover hover:scale-105 transition-transform duration-500"
                    />
                    {i === 0 && (
                      <div className="absolute top-4 right-4 bg-gold text-gold-foreground px-3 py-1 rounded-full text-sm font-semibold shadow-lg">
                        VIP
                      </div>
                    )}
                  </div>
                ))}
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* CTA Section */}
      <section className="py-20 bg-gradient-to-r from-foreground to-foreground/90 relative overflow-hidden">
        {/* Decorative elements */}
        <div className="absolute top-0 left-0 w-full h-1 bg-gradient-to-r from-gold via-gold-light to-gold" />
        <div className="absolute inset-0 opacity-5">
          <div className="absolute top-10 left-10 w-32 h-32 border border-gold rounded-full" />
          <div className="absolute bottom-10 right-10 w-48 h-48 border border-gold rounded-full" />
        </div>

        <div className="container mx-auto px-4 relative z-10">
          <div className="max-w-3xl mx-auto text-center animate-fade-in">
            <div className="inline-flex items-center gap-2 bg-gold/20 text-gold px-4 py-2 rounded-full mb-6">
              <Sparkles className="w-4 h-4" />
              <span className="text-sm font-medium">Exclusive Consultation</span>
            </div>
            
            <h2 className="text-4xl md:text-5xl font-bold text-white mb-6">
              Ready for Your <span className="text-gold">VIP Smile?</span>
            </h2>
            
            <p className="text-lg text-white/70 mb-10 max-w-xl mx-auto">
              Book your exclusive consultation today and discover the perfect cosmetic solution for your dream smile.
            </p>

            <div className="flex flex-col sm:flex-row gap-4 justify-center">
              <Button 
                size="lg"
                className="bg-gold hover:bg-gold-dark text-gold-foreground rounded-full px-8 py-6 text-lg font-semibold shadow-lg hover:shadow-xl transition-all"
              >
                <Phone className="w-5 h-5 mr-2" />
                Book Your VIP Consultation
              </Button>
              
              <Button 
                size="lg"
                variant="outline"
                className="border-2 border-white/30 text-white hover:bg-white/10 rounded-full px-8 py-6 text-lg font-semibold"
              >
                <MessageCircle className="w-5 h-5 mr-2" />
                Contact via WhatsApp
              </Button>
            </div>
          </div>
        </div>
      </section>

      <Footer />
    </div>
  );
};

export default CosmeticDentistry;
